                }}
                onScroll={updateScrollButtons}
              >
                {residentialImages.map((image, index) => (
                  <div 
                    key={index}
                    className="flex-shrink-0 group cursor-pointer"
                    style={{ minWidth: '400px', maxWidth: '400px' }}
