import React, { useEffect, useMemo, useState } from 'react';
import ProjectList from '../components/ProjectList';
import { getProjects } from '../api/projectApi';
import ImageCarousel from '../components/ImageCarousel';
import heroBg from '../Images/main.png';

const Home = () => {
  const [projects, setProjects] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    let mounted = true;
    getProjects()
      .then((data) => { if (mounted) setProjects(data); })
      .catch((e) => setError(e?.response?.data?.message || 'Failed to load projects'))
      .finally(() => setIsLoading(false));
    return () => { mounted = false; };
  }, []);

  const categories = useMemo(() => ['RESIDENTIAL', 'COMMERCIAL', 'SCHOOL', 'INTERIOR DESIGN'], []);
  const [activeCat, setActiveCat] = useState('RESIDENTIAL');

  return (
    <div>
      <section
        className="relative h-[60vh] rounded-xl overflow-hidden mb-10 flex items-center"
        style={{ backgroundImage: `url(${heroBg})`, backgroundSize: 'cover', backgroundPosition: 'center' }}
      >
        <div className="absolute inset-0 bg-black/35" />
        <div className="relative z-10 container mx-auto px-4">
          <h1 className="text-5xl sm:text-6xl font-extrabold text-white max-w-4xl">
            “WE DESIGN EXPERIENCES, WHERE EVERY DETAIL SHAPES HOW YOU FEEL”
          </h1>
        </div>
      </section>

      <section className="container mx-auto">
        <div className="flex items-center gap-6 justify-center mb-6">
          {categories.map((c) => (
            <button
              key={c}
              onClick={() => setActiveCat(c)}
              className={`uppercase tracking-wide text-sm ${activeCat === c ? 'text-black' : 'text-gray-500 hover:text-black'}`}
            >
              {c}
            </button>
          ))}
        </div>
        <ImageCarousel category={activeCat} />
      </section>

      <section className="mt-12">
        <h2 className="text-2xl font-semibold mb-4">All Projects</h2>
        <ProjectList projects={projects} isLoading={isLoading} error={error} />
      </section>
    </div>
  );
};

export default Home;
