import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import logo from '../Images/logo.png';

const Navbar = () => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <header className="absolute top-0 left-0 right-0 z-20" style={{backgroundColor: 'transparent'}}>
      <div className="container mx-auto flex items-center justify-between py-6 px-4" style={{maxWidth: '1200px', margin: '0 auto', display: 'flex', alignItems: 'center', justifyContent: 'space-between'}}>
        <Link to="/" style={{textDecoration: 'none'}}>
          <img src={logo} alt="MOE" className="h-16 w-auto" />
        </Link>
        <nav className="flex items-center gap-8">
          <a href="#top" className="text-white hover:text-gray-200 font-medium uppercase tracking-wide">HOME</a>
          <div className="relative">
            <button 
              onClick={() => setIsDropdownOpen(!isDropdownOpen)}
              className="text-white hover:text-gray-200 font-medium uppercase tracking-wide"
            >
              PROJECTS
            </button>
            {isDropdownOpen && (
              <div className="absolute pt-3 left-0 z-50">
                <div className="bg-white border rounded shadow-lg py-2 w-48">
                  <a href="#residential" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-50 cursor-pointer" onClick={() => setIsDropdownOpen(false)}>RESIDENTIAL</a>
                  <a href="#commercial" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-50 cursor-pointer" onClick={() => setIsDropdownOpen(false)}>COMMERCIAL</a>
                  <a href="#school" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-50 cursor-pointer" onClick={() => setIsDropdownOpen(false)}>SCHOOL</a>
                  <a href="#interior" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-50 cursor-pointer" onClick={() => setIsDropdownOpen(false)}>INTERIOR DESIGN</a>
                </div>
              </div>
            )}
          </div>
          <a href="#about" className="text-white hover:text-gray-200 font-medium uppercase tracking-wide">ABOUT</a>
          <a href="#contact" className="text-white hover:text-gray-200 font-medium uppercase tracking-wide">CONTACT</a>
        </nav>
        <div className="w-16"></div>
      </div>
    </header>
  );
};

export default Navbar;
