import React, { useState } from 'react';

const Navbar = () => {
  const [showDropdown, setShowDropdown] = useState(false);

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      const navbarHeight = 80;
      const elementPosition = element.offsetTop - navbarHeight;
      window.scrollTo({
        top: elementPosition,
        behavior: 'smooth'
      });
    }
  };

  return (
    <div style={{ 
      position: 'fixed', 
      top: 0, 
      left: 0,
      width: '100%', 
      height: '100vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      backgroundColor: 'transparent', 
      zIndex: 9999,
      pointerEvents: 'none'
    }}>
      <div style={{ 
        display: 'flex', 
        alignItems: 'center', 
        gap: '32px',
        pointerEvents: 'auto'
      }}>
        <button
          onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
          style={{
            background: 'none',
            border: 'none',
            color: 'white',
            fontSize: '14px',
            fontWeight: '500',
            cursor: 'pointer',
            padding: '12px 16px',
            userSelect: 'none'
          }}
        >
          HOME
        </button>

        <div style={{ position: 'relative' }}>
          <button
            onClick={() => setShowDropdown(!showDropdown)}
            style={{
              background: 'none',
              border: 'none',
              color: 'white',
              fontSize: '14px',
              fontWeight: '500',
              cursor: 'pointer',
              padding: '12px 16px',
              userSelect: 'none'
            }}
          >
            PROJECTS â–¶
          </button>

          {showDropdown && (
            <div style={{
              position: 'absolute',
              top: '100%',
              left: '0',
              backgroundColor: 'white',
              border: '1px solid #e5e7eb',
              borderRadius: '8px',
              boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
              padding: '8px 0',
              minWidth: '180px',
              marginTop: '8px',
              zIndex: 1001
            }}>
              <button
                onClick={() => {
                  setShowDropdown(false);
                  scrollToSection('residential');
                }}
                style={{
                  width: '100%',
                  background: 'none',
                  border: 'none',
                  padding: '12px 16px',
                  textAlign: 'left',
                  cursor: 'pointer',
                  fontSize: '14px',
                  color: '#374151'
                }}
              >
                Residential
              </button>
              <button
                onClick={() => {
                  setShowDropdown(false);
                  scrollToSection('commercial');
                }}
                style={{
                  width: '100%',
                  background: 'none',
                  border: 'none',
                  padding: '12px 16px',
                  textAlign: 'left',
                  cursor: 'pointer',
                  fontSize: '14px',
                  color: '#374151'
                }}
              >
                Commercial
              </button>
              <button
                onClick={() => {
                  setShowDropdown(false);
                  scrollToSection('school');
                }}
                style={{
                  width: '100%',
                  background: 'none',
                  border: 'none',
                  padding: '12px 16px',
                  textAlign: 'left',
                  cursor: 'pointer',
                  fontSize: '14px',
                  color: '#374151'
                }}
              >
                School
              </button>
              <button
                onClick={() => {
                  setShowDropdown(false);
                  scrollToSection('interior');
                }}
                style={{
                  width: '100%',
                  background: 'none',
                  border: 'none',
                  padding: '12px 16px',
                  textAlign: 'left',
                  cursor: 'pointer',
                  fontSize: '14px',
                  color: '#374151'
                }}
              >
                Interior
              </button>
            </div>
          )}
        </div>

        <button
          onClick={() => scrollToSection('about')}
          style={{
            background: 'none',
            border: 'none',
            color: 'white',
            fontSize: '14px',
            fontWeight: '500',
            cursor: 'pointer',
            padding: '12px 16px',
            userSelect: 'none'
          }}
        >
          ABOUT
        </button>

        <button
          onClick={() => scrollToSection('contact')}
          style={{
            background: 'none',
            border: 'none',
            color: 'white',
            fontSize: '14px',
            fontWeight: '500',
            cursor: 'pointer',
            padding: '12px 16px',
            userSelect: 'none'
          }}
        >
          CONTACT
        </button>
      </div>
    </div>
  );
};

export default Navbar;
