import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { getProjectById } from '../api/projectApi';

const ProjectDetail = () => {
  const { id } = useParams();
  const [project, setProject] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    let mounted = true;
    setIsLoading(true);
    getProjectById(id)
      .then((data) => {
        if (mounted) setProject(data);
      })
      .catch((e) => setError(e?.response?.data?.message || 'Failed to load project'))
      .finally(() => setIsLoading(false));
    return () => { mounted = false; };
  }, [id]);

  if (isLoading) return <div className="py-10 text-center">Loading…</div>;
  if (error) return <div className="py-10 text-center text-red-600">{error}</div>;
  if (!project) return null;

  return (
    <div className="max-w-4xl mx-auto">
      <Link to="/" className="text-sm text-gray-600 hover:underline">← Back</Link>
      <h1 className="text-3xl font-bold mt-2">{project.title}</h1>
      <p className="text-gray-700 mt-2">{project.description}</p>
      {project.images?.length ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 mt-6">
          {project.images.map((src, idx) => (
            <img key={idx} src={src} alt={`${project.title}-${idx}`} className="rounded" />
          ))}
        </div>
      ) : project.thumbnailUrl ? (
        <img src={project.thumbnailUrl} alt={project.title} className="rounded mt-6" />
      ) : null}
      {project.meta && (
        <div className="mt-6 text-sm text-gray-600">
          <pre className="bg-gray-100 p-3 rounded overflow-auto">{JSON.stringify(project.meta, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

export default ProjectDetail;
