import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import logo from '../Images/logo.png';

const Navbar = () => {
  const [showDropdown, setShowDropdown] = useState(false);
  const dropdownRef = useRef(null);

  // Smooth scroll function
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  // Close dropdown on outside click
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowDropdown(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <header
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        zIndex: 99999, // ✅ always above overlay
        backgroundColor: 'transparent'
      }}
    >
      <div
        style={{
          maxWidth: '1200px',
          margin: '0 auto',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between',
          padding: '20px 16px'
        }}
      >
        {/* Logo */}
        <Link
          to="/"
          style={{ textDecoration: 'none' }}
          onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
        >
          <img src={logo} alt="MOE" style={{ height: '56px', width: 'auto' }} />
        </Link>

        {/* Navigation */}
        <nav style={{ display: 'flex', alignItems: 'center', gap: '32px' }}>
          <span
            onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
            style={navItemStyle}
          >
            HOME
          </span>

          {/* Projects Dropdown */}
          <div ref={dropdownRef} style={{ position: 'relative' }}>
            <span
              onClick={() => setShowDropdown(!showDropdown)}
              style={navItemStyle}
            >
              PROJECTS {showDropdown ? '▲' : '▼'}
            </span>

            {showDropdown && (
              <div style={dropdownMenuStyle}>
                {['residential', 'commercial', 'school', 'interior'].map(
                  (section, idx) => (
                    <div
                      key={idx}
                      onClick={() => {
                        setShowDropdown(false);
                        scrollToSection(section);
                      }}
                      style={dropdownItemStyle}
                      onMouseEnter={(e) =>
                        (e.target.style.backgroundColor = '#f3f4f6')
                      }
                      onMouseLeave={(e) =>
                        (e.target.style.backgroundColor = 'white')
                      }
                    >
                      {section.toUpperCase()}
                    </div>
                  )
                )}
              </div>
            )}
          </div>

          <span onClick={() => scrollToSection('about')} style={navItemStyle}>
            ABOUT
          </span>

          <span onClick={() => scrollToSection('contact')} style={navItemStyle}>
            CONTACT
          </span>
        </nav>
      </div>
    </header>
  );
};

// ✅ Extracted reusable styles
const navItemStyle = {
  color: 'white',
  fontSize: '14px',
  fontWeight: '500',
  textTransform: 'uppercase',
  letterSpacing: '1px',
  cursor: 'pointer'
};

const dropdownMenuStyle = {
  position: 'absolute',
  top: '100%',
  left: '0',
  backgroundColor: 'white',
  border: '1px solid #e5e7eb',
  borderRadius: '8px',
  boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
  padding: '8px 0',
  minWidth: '180px',
  marginTop: '8px',
  zIndex: 1001
};

const dropdownItemStyle = {
  padding: '12px 16px',
  cursor: 'pointer',
  fontSize: '14px',
  color: '#374151',
  backgroundColor: 'white',
  transition: 'background-color 0.2s ease'
};

export default Navbar;
